function getUrlParams(prop){var params={};var search=decodeURIComponent(window.location.href.slice(window.location.href.indexOf("?")+1));var definitions=search.split("&");definitions.forEach(function(val,key){var parts=val.split("=",2);params[parts[0]]=parts[1]});return params[prop]}$(function(){var a=audiojs.createAll({css:false});var audio=a[0];var selectedName=getUrlParams("song");var $trackLink;if(selectedName===undefined){$trackLink=$(".music_play").first()}else{$trackLink=$(".music_play[title='"+selectedName+"']")}var audioSrc=$trackLink.attr("data-src");$trackLink.parent().addClass("playing");audio.load(audioSrc);$(".music_play").click(function(e){e.preventDefault();$(".music_play").parent().removeClass("playing");$(this).parent().addClass("playing");audio.load($(this).attr("data-src"));audio.play()});$(document).keydown(function(e){var unicode=e.charCode?e.charCode:e.keyCode;if(unicode==39){var next=$("li.playing").next();if(!next.length)next=$("ol li").first();next.click()}else if(unicode==37){var prev=$("li.playing").prev();if(!prev.length)prev=$("ol li").last();prev.click()}else if(unicode==32){audio.playPause()}})});
